import streamlit as st


st.title("Calculadora :P")
num1 = st.number_input("primer numero aca :P")
num2 = st.number_input("aca el segundo numero :P")
try:

    if st.button("SUMAR"):
        res = (num1 + num2)
        txt = st.header(f"{num1} + {num2} da {res}")
    if st.button("RESTAR"):
        res = (num1 - num2)
        txt = st.header(f"{num1} - {num2} da {res}")
    if st.button("MULTIPLICAR"):
        res = (num1 * num2)
        txt = st.header(f"{num1} x {num2} da {res}")
    if st.button("DIVIDIR"):
        if num2 == 0:
            txt = st.header("no se puede dividir por 0 >:(")
        else:
            res = (num1 / num2)
            txt = st.header(f"{num1} / {num2} da {res}")
except:
    txt = st.header("Oops.. algo salio mal :(")
st.image("data:image/jpeg;base64,", caption="blob")